import React, { useState } from 'react'
import ExpensesFilter from "./Expense Filter/ExpenseFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "./Card";

function Expenses(props) {
  const arr = props.expenseData;
  const [selectedYear, setSelectedYear]=useState("2022")
  const yearChangeHandler = (year) => {
    arr = props.expenseData.filter(el => el.date.getFullYear() === year);
    setSelectedYear(year);
  }

  return (
    <Card className="expenses">
      <div>
        <ExpensesFilter onYearChange={yearChangeHandler} expensesData={arr} />
      </div>
      {arr.map(item => <ExpenseItem expense={item}/>)}
    </Card>
  );
}
export default Expenses;
