import React, { useState } from 'react'
import ExpensesFilter from "./Expense Filter/ExpenseFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "./Card";

function Expenses(props) {
  const [filterdExpenses, setFilteredExpenses]=useState(props.expenseData)
  const yearChangeHandler = (selectedYear) => {
    const arr = props.expenseData.filter(el => el.date.getFullYear() === selectedYear);
    setFilteredExpenses(arr);
  }

  return (
    <Card className="expenses">
      <div>
        <ExpensesFilter onYearChange={yearChangeHandler} expensesData={props.expenseData} />
      </div>
      {setFilteredExpenses.map(item => <ExpenseItem expense={item}/>)}
    </Card>
  );
}
export default Expenses;
